import React, { useState } from "react";
import "./Accounts.css";
import { useSelector } from "react-redux";
import { selectAllAccounts } from "../../redux/selectors/accountsSelector";
import Account from "./Account";
import Button from "../common/Button";
import Modal from "../common/Modal";

function Accounts() {
    const [ isModalOpen, setModalOpen ] = useState(false);
    const accounts = useSelector(selectAllAccounts);

    const handleClick = () => {
        setModalOpen(true);
    };
    const closeModal = () => setModalOpen(false);

    return (
        <div className="accounts">
            <div className="card-header">
                Accounts
                <Button label="Add" className="button-add" onClick={handleClick} />
            </div>
            {/* Checks if there is an existing account */}
            {!accounts ? <p>Please add an account</p>
            : <div className="accounts-list">
                {/* maps through the state and renders account */}
                {Object.entries(accounts).map(([key, value]) => {
                    const { name, type, currentBalance } = value;
                    const id = key;

                    return (
                        <Account
                            id={id}
                            name={name}
                            type={type}
                            currentBalance={currentBalance}
                        />
                    )
                })}
            </div> 
            }
            {/* Account Form */}
            <Modal isOpen={isModalOpen} onClose={closeModal}  >

            </Modal>
        </div>
    );
};

export default Accounts;
