    15: { type: "expense", description: "2/4 Koran Street Food", date: "26/7/2024", amount: 4.94, category: "Eat Out" },
    16: { type: "expense", description: "KFC Dash", date: "26/7/2024", amount: 18.45, category: "Eat Out" },
    17: { type: "expense", description: "Coles Grocery", date: "26/7/2024", amount: 33.6, category: "Grocery" },
