// Select all budgets
 export const selectAllBudgets = (state) => {
    if (!state) {
        return [];
    }

    const { byCategory } = state.budgets;
    const selectedBudget = Object.keys(byCategory).map( category => byCategory[category]);
    console.log(selectedBudget);
    return selectedBudget
 };
