import React from "react";
import { Doughnut } from "react-chartjs-2";
import "./chart.css";
import {Chart, ArcElement} from 'chart.js'
Chart.register(ArcElement)

function ExpenseChart({expenses}) {
    const expenseData = expenses && expenses.length > 0 ? expenses.map(expense => expense.totalSpent) : [];
    const categories = expenses && expenses.length > 0 ? expenses.map(expense => expense.category) : [];
    console.log("chart categories",categories)
    const totalSpent = expenseData.reduce((sum, expense) => sum + expense, 0);
    const data={
        labels: categories,
        datasets: [
            {
                label: "expense",
                data: expenseData,
                backgroundColor: [
                    'rgb(255, 99, 132)',
                    'rgb(54, 162, 235)',
                    'rgb(255, 205, 86)'
                ],
                hoverOffset: 1,
                borderColor: "transparent",
            }
        ]
    };

    const options = {
        plugins: {
            legend: {
                display: false, // Show the legend
            },
            tooltip: {
                callbacks: {
                    label: function(context) {
                        let label = context.label || '';

                        if (label) {
                            label += ': ';
                        }
                        label += context.raw;
                        return label;
                    }
                }
            },
            // Custom plugin to add text in the center of the doughnut
            centerText: {
                text: `$${totalSpent.toLocaleString()}`,
            }
        },
        cutout: "70%"
    };

    const centerTextPlugin = {
        id: 'centerText',
        beforeDraw: function (chart) {
            const { ctx, width, height } = chart;
            ctx.save();
            const fontSize = (height / 150).toFixed(2);
            ctx.font = `${fontSize}em sans-serif`;
            ctx.textAlign = 'center';
            ctx.textBaseline = 'middle';
            ctx.fillStyle = '#000';

            const text = `$${totalSpent.toLocaleString()}`;
            ctx.fillText(text, width / 2, height / 2);

            ctx.restore();
        }
    };

    return (
        <div className="chart-container">
            {categories && categories.length > 0 ? (
                <Doughnut 
                className="chart-expense" 
                data={data} 
                options={options}
                plugins={[centerTextPlugin]}
                />
            ) : (
                <p>No data found</p>
            )}
        </div>
    );
};

export default ExpenseChart;
