// Select all budgets
 export const selectAllBudgets = (state) => {
    if (!state) {
        return [];
    }

    const { byCategory } = state.budgets;
    if (!byCategory) {
        return [];
    }
    return Object.keys(byCategory).map( category => byCategory[category]);
 };
