import React, { useState } from "react";


function Filter({transactions, onFilterChange}) {
    const filterOptions = transactions.map(transaction => transaction.date)
    const handleFilterChange = (e) => {
        const date = new Date(e.target.value);
        if (value === "all") {
            onFilterChange(null);
        };

        onFilterChange({
            month: date.getMonth(),
            year: date.getFullYear()
        });
    };



    return (
        <div className="filter-container">
            <select onChange={handleFilterChange}>
                <option value="all">All</option>
                {filterOptions.map(date => {
                    const dateOption = new Date(date);
                    const month = dateOption.toLocaleString("en-us", {month: "long"});
                    const year = dateOption.getFullYear();

                    return <option value={`${month} ${year}`} >{month} {year}</option>
                })}
            </select>
        </div>
    );
};

export default Filter
