import React from "react";
import { Doughnut } from "react-chartjs-2";


function ExpenseChart({categories, expenses}) {
    const expenseData = expenses && expenses.length > 0 ? expenses.map(expense => expense.amount) : [];
    const data=[{
        labels: categories,
        datasets: [
            {
                label: "expense",
                data: expenseData,
                backgroundColor: [
                    "red",
                    "blue",
                    "yellow"
                ]
            }
        ]
    }];

    return (
        <div className="chart-expense">
            {categories && categories.length > 0 ? (
                <Doughnut data={data}/>
            ) : (
                <p>No data found</p>
            )}
        </div>
    )
};

export default ExpenseChart;
