import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    byId: {
      1: {
        type: "income",
        description: "ECA - Salary",
        date: "July 7, 2024",
        amount: 1521.25,
        category: "Salary",
      }
    },
    allIds: [1],
    totals: {income: 0, expenses: 0, balance: 0}
};

const transactionsSlice = createSlice({
  name: "transactions",
  initialState,
  reducers: {
    addTransaction: (state, action) => {
      const { id } = action.payload;
      console.log("Action Payload: ", action.payload)
      console.log('State before update:', JSON.parse(JSON.stringify(state)));

      state.byId[id] = action.payload;
      state.allIds.push(id);

      console.log('State after update:', JSON.parse(JSON.stringify(state)));
    },
    calculateTotals: (state) => {
      // Maps through all the IDs and updates the totals object
      state.allIds.map((id) => {
        if (state.byId[id].type === "income") {
            state.totals.income += state.byId[id].amount;
        } else {
          state.totals.expenses += state.byId[id].amount;
        }
      });
      state.totals[balance] = state.totals.income - state.totals.expenses;
    },
  },
});

export const { addTransaction } = transactionsSlice.actions;
export default transactionsSlice.reducer;
