import React, {useEffect} from "react";
import Transaction from "../Transaction/Transaction";
import "./Transactions.css";
import OptionsMenu from "../common/OptionsMenu";

function Transactions({transactions}) {

    useEffect(() => {
        console.log("Transactions component re-rendered with transactions:", transactions);
      }, [transactions]);

    if (!transactions || transactions.length === 0) {
        return <p>No transactions to display.</p>;
    }

    // Sort transactions by date in descending order
    const sortedTransactions = transactions.sort((a, b) => new Date(b.date) - new Date(a.date));

    return (
        <div className="card transactions">
            <div className="card-header">
                Transaction
                <OptionsMenu >
                    <div className="dropdown-item" onClick={() => handleForm("account")}>
                        Add
                    </div>
                    <div className="dropdown-item" onClick={() => handleEdit()}>
                        Edit
                    </div>
                    <div className="dropdown-item" onClick={() => handleForm("account", "transfer")}>
                        Transfer
                    </div>
                </OptionsMenu>
            </div>
            <div className="transactions-list">
                {sortedTransactions.map((transaction) => {
                    const { id, description, amount, category, date, type } = transaction;

                    return ( 
                        <Transaction
                            key={id}
                            id={id}
                            description={description}
                            amount={amount}
                            category={category}
                            date={date}
                            type={type}
                        />
                    );
                })}
            </div>
        </div>
    );
};

export default Transactions;
