import React, { useState } from "react";
import "./TransactionForm.css";
import { useDispatch } from "react-redux";
import { addTransaction } from "../../redux/slices/transactionsSlice";

function TransactionForm({closeModal}) {
    const [ formData, setFormData ] = useState({
        type: "income",
        description: "",
        date: "",
        amount: "",
        category: "",
    });

    const dispatch = useDispatch();

    const handleChange = e => {
        setFormData({
            ...formData, 
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const newTransaction = {
            ...formData,
            id: Date.now(),
            amount: parseFloat(formData.amount)
        };

        dispatch(addTransaction(newTransaction))
        closeModal();
    }

    return (
        <div className={`TransactionForm`}>
            <form >
                <select placeholder="Transaction Type" name="type" value={formData.type} onChange={handleChange} >
                    <option value="income">Income</option>
                    <option value="expense">Expense</option>
                </select>
                <input type="text" placeholder="Description" name="description" value={formData.description} onChange={handleChange} />
                <input type="number" placeholder="Amount" name="amount" value={formData.amount} onChange={handleChange} />
                <input type="date" placeholder="Date" name="date" value={formData.value} onChange={handleChange} />
                {/*!! TO DO To validate category */}
                <input type="text" placeholder="Category" name="category" value={formData.category} onChange={handleChange} />
                <button className="button-txn" type="submit">Add Transaction</button>
            </form>
        </div>
    )
};

export default TransactionForm;
