//     },
//     {
//         id: 546781,
//         type: "expense",
//         description: "Rent",
//         date: "July 4, 2024",
//         amount: 1521.25,
//         category: "Salary",
//     },
// ]

function Transactions() {
    const transactions = useSelector(selectAllTransactions);

    if (!transactions.byId || !transactions.allIds) {
        return <p>No transactions to display.</p>;
    }

    return (
        <div className="TransactionsList">
            {transactions.allIds.map((id) => {
                const transaction = transactions.byId[id];
                if (!transaction) {
                    return null;
                }

                const { description, amount, category, date, type } = transaction;

                return ( 
                    <Transaction
                        key={id}
                        id={id}
                        description={description}
                        amount={amount}
                        category={category}
                        date={date}
                        type={type}
                    />
                );
        })}
        </div>
    );
};

export default Transactions;
