import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    transactions: {
       byId: {},
       allIds: []       
    },
    status: "idle",
    error: null
};


const transactionsSlice = createSlice({
    name: "transactions",
    initialState, 
    reducers: {
        addTransaction: (state, action) => {
            const { id } = action.payload
            console.log("state before update:", JSON.parse(JSON.stringify(state.transactions)))
            state.transactions.byId[id] = action.payload;
            state.transactions.allIds.push(id);
            console.log("Transaction added:", JSON.parse(JSON.stringify(state.transactions)))
        },
    },
});

//  Select transactions by type
export const { addTransaction } = transactionsSlice.actions;
export default transactionsSlice.reducer;
