import { Icon } from "@iconify/react/dist/iconify.js";
import React, { useState } from "react";


function Filter({transactions}) {
    const [ isFilterOpen, setFilterOpen ] = useState(false);

    const filterOptions = transactions.map(transaction => transaction.date)

    const toggle = () => {
        setFilterOpen(!isFilterOpen);
    };
    const handleFilterChange = () => {

    };



    return (
        <div className="filter">
            {!isFilterOpen 
            ? <div className="icon-container">
                <Icon className="filter-close" icon="ri:arrow-drop-down-line" onClick={toggle} />
            </div> 
            : <div className="filter-container">
                <div className="icon-container">
                    <Icon className="filter-open" icon="ri:arrow-drop-up-line" onClick={toggle} />
                </div>
                <select>
                    {filterOptions.map(date => {
                        const dateOption = new Date(date);
                        const month = dateOption.toLocaleString("en-us", {month: "short"});
                        const year = dateOption.getFullYear();

                        return <option >{month}{year}</option>
                    })}
                </select>
            </div>
            }

        </div>
    );
};

export default Filter
