import React from "react";
import { Doughnut } from "react-chartjs-2";
import {Chart, ArcElement} from 'chart.js'
import './chart.css';
Chart.register(ArcElement);

function ExpenseChart({ expenses }) {
    const expenseData = expenses && expenses.length > 0 ? expenses.map(expense => expense.totalSpent) : [];
    const categories = expenses && expenses.length > 0 ? expenses.map(expense => expense.category) : [];

    // Calculate total and gain
    const totalAmount = expenseData.reduce((sum, expense) => sum + expense, 0);
    const totalGain = 31587; // Example value, you should calculate or fetch this
    const gainPercentage = (totalGain / totalAmount) * 100;

    const data = {
        labels: categories,
        datasets: [
            {
                label: "expense",
                data: expenseData,
                backgroundColor: [
                    'rgb(255, 99, 132)',
                    'rgb(54, 162, 235)',
                    'rgb(255, 205, 86)',
                    'rgb(75, 192, 192)',
                    'rgb(153, 102, 255)',
                    'rgb(255, 159, 64)',
                    'rgb(201, 203, 207)'
                ],
                hoverOffset: 4,
                borderColor: "transparent",
            }
        ]
    };

    const options = {
        plugins: {
            legend: {
                display: false, // Hide the legend
            },
            tooltip: {
                callbacks: {
                    label: function(context) {
                        let label = context.label || '';

                        if (label) {
                            label += ': ';
                        }
                        label += context.raw;
                        return label;
                    }
                }
            },
            // Custom plugin to add text in the center of the doughnut
            centerText: {
                text: `Total Expenses`,
            }
        },
        cutout: '70%', // Make the center hollow
    };

    // Custom plugin for center text
    const centerTextPlugin = {
        id: 'centerText',
        beforeDraw: function(chart) {
            const { ctx, width, height } = chart;
            ctx.save();
            const fontSize = (height / 150).toFixed(2);
            ctx.font = `${fontSize}em sans-serif`;
            ctx.textAlign = 'center';
            ctx.textBaseline = 'middle';
            ctx.fillStyle = '#000';

            const lines = options.plugins.centerText.text.split('\n');
            let textY = height / 2;
            lines.forEach((line, index) => {
                ctx.fillText(line, width / 2, textY);
                textY += 20; // Adjust the line spacing
            });

            ctx.restore();
        }
    };

    return (
        <div className="chart-container">
            {categories && categories.length > 0 ? (
                <Doughnut
                    className="chart-expense"
                    data={data}
                    options={options}
                    plugins={[centerTextPlugin]} // Register custom plugin
                />
            ) : (
                <p>No data found</p>
            )}
        </div>
    );
}

export default ExpenseChart;
