import React, {useEffect} from "react";
import Transaction from "../Transaction/Transaction";
import { useSelector } from "react-redux";
import { selectAllTransactions } from "../../redux/selectors/transactionsSelectors";


function Transactions() {
    const transactions = useSelector(selectAllTransactions);

    useEffect(() => {
        console.log("Transactions component re-rendered with transactions:", transactions);
      }, [transactions]);

    if (!transactions || transactions.length === 0) {
        return <p>No transactions to display.</p>;
    }

    return (
        <div className="TransactionsList">
            {transactions.map((transaction) => {
                const { id, description, amount, category, date, type } = transaction;

                return ( 
                    <Transaction
                        key={id}
                        id={id}
                        description={description}
                        amount={amount}
                        category={category}
                        date={date}
                        type={type}
                    />
                );
            })}
        </div>
    );
};

export default Transactions;
