export function formatCurrency(amount) {
    return new Intl.NumberFormat('en-au', {
        style: "currency",
        currency: "AUD",
        minimumFractionDigits: 2,
        maximumFractionDigits: 2,
    }).format(amount);
}

export function getThisMonth() {
    const date = new Date();
    return date.toISOString().split('T')[0].slice(0, 7);
};
