import React from "react";
import { Doughnut } from "react-chartjs-2";
import "./chart.css";
import { Chart, ArcElement } from 'chart.js';
Chart.register(ArcElement);

function ExpenseChart({ expenses }) {
    const expenseData = expenses && expenses.length > 0 ? expenses.map(expense => expense.totalSpent) : [];
    const categories = expenses && expenses.length > 0 ? expenses.map(expense => expense.category) : [];
    console.log("chart categories", categories);
    const totalSpent = expenseData.reduce((sum, expense) => sum + expense, 0);

    const data = {
        labels: categories,
        datasets: [
            {
                label: "expense",
                data: expenseData,
                backgroundColor: [
                    'rgb(255, 99, 132)',
                    'rgb(54, 162, 235)',
                    'rgb(255, 205, 86)'
                ],
                hoverOffset: 1,
                borderColor: "transparent",
            }
        ]
    };

    const options = {
        plugins: {
            legend: {
                display: false, // Hide the legend
            },
            tooltip: {
                callbacks: {
                    label: function (context) {
                        let label = context.label || '';

                        if (label) {
                            label += ': ';
                        }
                        label += context.raw;
                        return label;
                    }
                }
            },
        },
        cutout: "70%" // Hollow center for text
    };

    const centerTextPlugin = {
        id: 'centerText',
        beforeDraw: function (chart) {
            const { ctx, width, height } = chart;
            ctx.save();

            // Set font and text properties
            const fontSize = (height / 150).toFixed(2);
            ctx.font = `18px inter`;
            ctx.textAlign = 'center';
            ctx.textBaseline = 'middle';
            ctx.fillStyle = '#F1F0F5'; // Custom color for the text

            // Text to be displayed in the center
            const text = `Total Expenses\n$${totalSpent.toLocaleString()}`;
            const textLines = text.split('\n');

            // Display each line of text
            const lineHeight = 20;
            let textY = height / 2 - (lineHeight / 2) * (textLines.length - 1);
            textLines.forEach((line) => {
                ctx.fillText(line, width / 2, textY);
                textY += lineHeight;
            });

            ctx.restore();
        }
    };

    return (
        <div className="chart-container">
            {categories && categories.length > 0 ? (
                <Doughnut
                    className="chart-expense"
                    data={data}
                    options={options}
                    plugins={[centerTextPlugin]}
                />
            ) : (
                <p>No data found</p>
            )}
        </div>
    );
}

export default ExpenseChart;
