import React, { useState } from "react";
import "./TransactionsView.css";
import Transactions from "./Transactions";
import { useSelector } from "react-redux";
import { selectAllBudgets } from "../../redux/selectors/budgetsSelector";
import Budgets from "../Budgets/Budgets";
import { selectTransactionsByMonth, selectTransactionsByType } from "../../redux/selectors/transactionsSelectors";
import Button from "../common/Button";
import Filter from "../common/Filter";


function TransactionsView() {
    const [ filterDate, setFilterDate ] = useState("all");
    const transactions = useSelector(state => state.transactions);
    const filteredTransactionsByMonth = useSelector(state => selectTransactionsByMonth(state, filterDate.month, filterDate.year))
    const budgets = useSelector(selectAllBudgets);

    const filterTransactions = (type) => {
        if (filterDate === "all") {
            if (type === "income") {
                return incomeTransactions;
            } else if (type === "expense") {
                return expenseTransactions;
            }
        };

        return filteredTransactionsByMonth.filter(transaction => transaction.type === type);
    };

    return (
        <div className="grid TransactionsView"> 
            <div className="card income-transactions">
                <div className="card-header">
                    Income
                    <Filter transactions={transactions} onFilterChange={setFilterDate} />
                    <Button label="Add Income" className="button-add" />
                </div>
                <Transactions transactions={() => filterTransactions("income")} budgets={budgets} />
            </div>
            <div className="card expense-transactions">
                <div className="card-header">
                    Expenses
                    <Button label="Add Expense" className="button-add" />
                </div>
                <Transactions transactions={() => filterTransactions("expense")} budgets={budgets} />
            </div>
            <div className="card budgets-container">
                <Budgets budgets={budgets} />
            </div>
        </div>
    )
};

export default TransactionsView;
